    public void signIN(){
        
        String cx = jTextField1.getText();
        String passphrase = jTextField2.getText();
        
        boolean exist = false;
        
        for(User u : LoginU.user){
            if (u.usr.equals(cx) && u.password.equals(passphrase)) {
                exist = true;
                
                if (u.role == 1) {
                    pManager m = new pManager();
                    m.setVisible(true);
                }else{
                    vSeller s = new vSeller();
                    s.setVisible(true);
                }
                
            clean();
            
            }
        }
        
        
        if (exist == false) {
            JOptionPane.showMessageDialog(this, "sorry, user or passwor wrong \n try again pls");
        }
        
    }
    
    public void clean(){
        jTextField1.setText("");
        jTextField2.setText("");
    }
   

